mapper.select:
SELECT d.pollutecode, FORMAT(MAX(`datavalue`), st.numprecision) AS maximumValue, FORMAT(MIN(`datavalue`), st.numprecision) AS minimumValue, FORMAT(AVG(`datavalue`), st.numprecision) AS averageVlaue, COUNT(*) AS sumValue, SUM(CASE WHEN d.dataflag!='F' THEN 1 ELSE 0 END ) AS validValue FROM (${RTTable}) d JOIN T_DIC_STDPOLLCODE st ON st.pollutecode = d.pollutecode WHERE d.datatime >= #{startTime} AND d.datatime <= #{endTime} AND d.pointcode IN
#{pointCode}
AND d.pollutecode IN
#{polluteCode}
GROUP BY pollutecode ORDER BY d.datatime ASC

SELECT A.POINTCODE, B.POINTNAME, SUBSTR(A.DATATIME,1,19) DATATIME, A.POLLUTECODE, B.POLLUTENAME, FORMAT(A.DATAVALUE, B.NUMPRECISION) AS DATAVALUE, A.DATAFLAG, C.SUNIT,FORMAT(C.SMINVALUE, B.NUMPRECISION) AS SMINVALUE,FORMAT(C.SMAXVALUE, B.NUMPRECISION) AS SMAXVALUE FROM (${RTTable}) A INNER JOIN V_ENV_STATIONPOLLINFO B ON A.POLLUTECODE = B.POLLUTECODE AND A.POINTCODE=B.POINTCODE
LEFT JOIN V_BAS_POLLSTD C ON C.POINTCODE = A.POINTCODE AND C.POLLUTECODE=A.POLLUTECODE AND C.SYSTEMTYPE =#{systemType}
WHERE A.DATATIME >= #{startTime} AND A.DATATIME <= #{endTime}
AND A.POINTCODE IN
#{pointCode}
AND A.POLLUTECODE IN
#{polluteCode}
ORDER BY A.DATATIME ASC

SELECT distinct C.DATATIME,C.POLLUTECODE,C.POLLUTENAME,C.DATAVALUE,C.POLLCONCUNIT FROM ( SELECT A.DATATIME, A.POLLUTECODE,B.POLLUTENAME, A.DATAVALUE, B.POLLCONCUNIT FROM (${RTTable}) A LEFT JOIN V_ENV_STATIONPOLLINFO B ON A.POLLUTECODE = B.POLLUTECODE AND A.POINTCODE = B.POINTCODE AND B.SYSTEMTYPE =#{systemType} WHERE A.DATATIME >= #{startTime} AND A.DATATIME <= #{endTime}
AND A.POINTCODE IN
#{pointCode}
AND A.POLLUTECODE IN
#{polluteCode}
GROUP BY A.DATATIME ,A.POLLUTECODE,B.POLLUTENAME,B.POLLCONCUNIT ) C ORDER BY C.DATATIME ASC

SELECT t.originallyDataCount,(CASE WHEN t.datacount>t.originallyDataCount THEN 0 ELSE (t.originallyDataCount-t.datacount) END) AS DefectDataCount, (CASE t.datacount WHEN 0 THEN 0 ELSE (CASE WHEN t.datacount>t.originallyDataCount THEN 1 ELSE t.datacount/t.originallyDataCount END) END)*100 AS dataIntegrityRate, t.pointcode,t.pointname,t.regionname FROM (SELECT TRUNCATE(TIMESTAMPDIFF(MINUTE,#{startTime}, DATE_ADD(#{endTime}, INTERVAL 1 SECOND))/p.micycletime,0) AS originallyDataCount, a.regionname, p.micycletime, p.rtcytletime AS rtCycleTime, p.pointcode, p.pointname, (case ISNULL(n.datacount) when 0 then n.datacount else 0 end) datacount FROM V_BAS_POINTSITE_INFO p LEFT JOIN (SELECT pointcode,COUNT(*) AS datacount FROM (SELECT k.pointcode,k.datatime FROM (${RTTable}) k WHERE k.datatime >= #{startTime} AND k.datatime <= #{endTime} GROUP BY k.pointcode,SUBSTR(k.datatime,1,16)) t GROUP BY pointcode) n ON p.pointcode = n.pointcode JOIN T_DIC_AREACODE a ON p.regioncode = a.code_region WHERE p.systemtype = #{systemType} AND p.pointcode IN
#{pointCode}
) t
