mapper.select:
SELECT distinct C.DATATIME,C.POLLUTECODE,C.POLLUTENAME,C.DATAVALUE,C.POLLCONCUNIT FROM ( SELECT A.DATATIME, A.POLLUTECODE,B.POLLUTENAME, A.AVGVALUE AS DATAVALUE, B.POLLCONCUNIT FROM
T_ENV_MONI_AIR_DATASATA A
T_PS_BAS_GAS_DATASATA A
T_PS_BAS_WAT_DATASATA A
LEFT JOIN V_ENV_STATIONPOLLINFO B ON A.POLLUTECODE = B.POLLUTECODE and A.POINTCODE = B.POINTCODE WHERE A.DATATIME >= #{startTime} AND A.DATATIME <= #{endTime}
AND A.POINTCODE IN
#{pointCode}
AND A.POLLUTECODE IN
#{polluteCode}
GROUP BY A.DATATIME ,A.POLLUTECODE,B.POLLUTENAME,B.POLLCONCUNIT ) C ORDER BY C.DATATIME ASC
